// components/OnboardingPage.js

import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { SignedIn, useClerk } from '@clerk/clerk-react';
import SignupSignInPopup from './SignUpSignInPopup';
import '../App.css';

const OnboardingPage = () => {
  const [isPopupOpen, setPopupOpen] = useState(false);
  const history = useHistory(); // Use useHistory here
  const { signedIn } = useClerk();

  const handleGetStartedClick = () => {
    setPopupOpen(true);
  };

  const handleClosePopup = () => {
    setPopupOpen(false);
  };

  useEffect(() => {
    // Check if the user is signed in
    if (signedIn) {
      history.push('/dashboard'); // Replace '/dashboard' with your actual dashboard route
    }
  }, [signedIn, history]);

  return (
    <div>
      <header>
        <h1 className="header-title">Rentora</h1>
      </header>
      <Link to="#" className="get-started-btn" onClick={handleGetStartedClick}>
        Get Started
      </Link>
      {isPopupOpen && <SignupSignInPopup onClose={handleClosePopup} />}
    </div>
  );
};

export default OnboardingPage;
