import React, { useEffect } from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn, useClerk } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();
  const { addListener, session } = useClerk();
  const isSignedIn = session && session.user;

  const handleCloseClick = () => {
    onClose();
  };

  let unsubscribe; // Declare the variable here

  useEffect(() => {
    console.log('useEffect is running');

    unsubscribe = addListener((resources) => {
      console.log('Event listener triggered:', resources);

      if (resources.session && resources.session.user) {
        // Authentication status has changed, user is signed in
        console.log('Sign-in successful');

        // Redirect to the dashboard or perform other actions
        navigate('/dashboard');

        // Unsubscribe to the listener once the navigation is done
        unsubscribe();
      }
    });

    // Clean up the listener when the component is unmounted
    return () => {
      console.log('Cleanup: Unsubscribe');
      unsubscribe();
    };
  }, [navigate, addListener]);

  console.log('Component rendered');

  return (
    <ClerkLoaded>
      <div className="popup-overlay">
        <div className="popup-content" style={popupStyles}>
          <button className="close-button" onClick={handleCloseClick}>
            X
          </button>
          <SignedOut>
            <div className="clerk-signin-container">
              <SignIn />
            </div>
          </SignedOut>
          <SignedIn>
            <Dashboard onClose={handleCloseClick} />
          </SignedIn>
        </div>
      </div>
    </ClerkLoaded>
  );
};


const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
