// components/SignupSignInPopup.js

import React from 'react';
import '../App.css';
import { ClerkProvider } from "@clerk/clerk-react";

const SignupSignInPopup = ({ onClose }) => {
  const handleCloseClick = () => {
    onClose(); // Call the onClose function to close the popup
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        {/* Add your signup/signin form components */}
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkProvider publishableKey={clerkPubKey}>
          <div>Hello from clerk</div>
        </ClerkProvider>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%', // Adjust as needed
  left: '50%', // Adjust as needed
  transform: 'translate(-50%, -33.4%)', // Center the popup
  width: '1096px',
  height: '633px',
  borderRadius: '50px 50px 0px 0px', // Pointed bottom corners, curved top
  background: '#FFF',
  padding: '20px', // Add padding as needed
  overflow: 'hidden', // Hide overflowing content outside the border-radius
};

export default SignupSignInPopup;
