import React, { useEffect } from 'react';
import { ClerkProvider, ClerkLoaded, SignedIn, SignedOut } from "@clerk/clerk-react";
import SignInForm from './SignInForm';
import Dashboard from './Dashboard';
import { useNavigate } from 'react-router-dom';

if (!process.env.REACT_APP_CLERK_PUBLISHABLE_KEY) {
  throw new Error("Missing Publishable Key");
}
const clerkPubKey = process.env.REACT_APP_CLERK_PUBLISHABLE_KEY;

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();

  const handleCloseClick = () => {
    onClose();
  };

  const handleSignIn = () => {
    // Perform sign-in logic
    // Assuming successful sign-in, navigate to the dashboard
    navigate('/onboarding');
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkProvider publishableKey={clerkPubKey}>
          <ClerkLoaded>
            <div className="clerk-signin-container">
              <SignedOut>
                <SignInForm onSignIn={handleSignIn} />
              </SignedOut>
              <SignedIn>
                <Dashboard onClose={handleCloseClick} />
              </SignedIn>
            </div>
          </ClerkLoaded>
        </ClerkProvider>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
