// SignupSignInPopup.js
import React, { useState } from 'react';
import { ClerkProvider } from "@clerk/clerk-react";
import '../App.css';

const SignupSignInPopup = ({ onClose, onSignIn }) => {
  const [isSignedIn, setIsSignedIn] = useState(false);

  const handleCloseClick = () => {
    onClose(); // Call the onClose function to close the popup
  };

  const handleSignInClick = () => {
    // Perform your sign-in logic here
    // For example, you can use Clerk's signIn function
    // Assuming Clerk.signIn returns a promise
    Clerk.signIn()
      .then(() => {
        setIsSignedIn(true);
        onSignIn(); // Callback to handle sign-in success
      })
      .catch((error) => {
        console.error('Sign in failed:', error);
      });
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        {!isSignedIn ? (
          <>
            <button className="close-button" onClick={handleCloseClick}>
              X
            </button>
            <ClerkProvider>
              <div>
                {/* Add your Clerk signup/signin form components here */}
                <button onClick={handleSignInClick}>Sign In</button>
              </div>
            </ClerkProvider>
          </>
        ) : (
          <div>
            {/* Display content for a signed-in user */}
            <p>Welcome, you are signed in!</p>
          </div>
        )}
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%', // Adjust as needed
  left: '50%', // Adjust as needed
  transform: 'translate(-50%, -33.4%)', // Center the popup
  width: '1096px',
  height: '633px',
  borderRadius: '50px 50px 0px 0px', // Pointed bottom corners, curved top
  background: '#FFF',
  padding: '20px', // Add padding as needed
  overflow: 'hidden', // Hide overflowing content outside the border-radius
};

export default SignupSignInPopup;
