import React, { Component } from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn, useClerk } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

class SignupSignInPopup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSignedIn: false,
    };
    this.navigate = useNavigate();
  }

  handleCloseClick = () => {
    this.props.onClose();
  };

  handleSignIn = () => {
    // Perform sign-in logic
    // Assuming successful sign-in, navigate to the dashboard
    this.navigate('/dashboard');
  };

  handleAuthenticationChange = (resources) => {
    if (resources.session && resources.session.user) {
      // Authentication status has changed, user is signed in
      console.log('Sign-in successful');

      // Set the state to indicate that the user is signed in
      this.setState({ isSignedIn: true });

      // Redirect to the dashboard or perform other actions
      this.navigate('/dashboard');
    }
  };

  componentDidMount() {
    // Subscribe to the listener when the component mounts
    this.unsubscribe = window.Clerk.addListener(this.handleAuthenticationChange);
  }

  componentWillUnmount() {
    // Unsubscribe from the listener when the component is unmounted
    if (this.unsubscribe) {
      this.unsubscribe();
    }
  }

  render() {
    return (
      <ClerkLoaded>
        <div className="popup-overlay">
          <div className="popup-content" style={popupStyles}>
            <button className="close-button" onClick={this.handleCloseClick}>
              X
            </button>
            <SignedOut>
              <div className="clerk-signin-container">
                <SignIn>
                  <div>
                    <h2>Sign In</h2>
                    <p>Enter your credentials to sign in.</p>
                    <button onClick={this.handleSignIn}>Sign In</button>
                  </div>
                </SignIn>
              </div>
            </SignedOut>
            <SignedIn>
              <Dashboard onClose={this.handleCloseClick} />
            </SignedIn>
          </div>
        </div>
      </ClerkLoaded>
    );
  }
}

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
