import React, { useEffect } from 'react';
import { SignIn, SignedIn } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';

const SignInForm = () => {
  const navigate = useNavigate();

  // useEffect to listen for authentication status changes
  useEffect(() => {
    // Use Clerk's isSignedIn to check if the user is signed in
    if (window.Clerk && window.Clerk.isSignedIn) {
      navigate('/dashboard');
    }
  }, [navigate]);

  return (
    <div>
      <SignedIn>
        {/* If already signed in, redirect immediately */}
        {() => navigate('/dashboard')}
      </SignedIn>

      <SignIn>
        <div>
          <h2>Sign In</h2>
          <p>Enter your credentials to sign in.</p>
        </div>
      </SignIn>
    </div>
  );
};

export default SignInForm;
