import React, { useEffect } from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn, useClerk } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();
  const { signOut, signIn } = useClerk();

  const handleCloseClick = () => {
    onClose();
  };

  const handleSignIn = async () => {
    // Perform sign-in logic
    // Assuming successful sign-in
    await signIn(); // Use Clerk's signIn method

    // Navigate to the dashboard
    navigate('/dashboard');
  };

  // useEffect to listen for authentication status changes
  useEffect(() => {
    // Use Clerk's isSignedIn to check if the user is signed in
    if (window.Clerk && window.Clerk.isSignedIn) {
      handleSignIn(); // Redirect immediately after sign-in
    }
  }, [navigate]);

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkLoaded>
          <div className="clerk-signin-container">
            <SignedOut>
              <SignIn>
                <div>
                  <h2>Sign In</h2>
                  <p>Enter your credentials to sign in.</p>
                </div>
              </SignIn>
            </SignedOut>
            <SignedIn>
              {/* Use the signOut method from useClerk */}
              <Dashboard onClose={handleCloseClick} signOut={signOut} />
            </SignedIn>
          </div>
        </ClerkLoaded>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
