import React from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn, useClerk } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();
  const { openSignIn, session } = useClerk();

  const handleCloseClick = () => {
    onClose();
  };

  // Redirect to the dashboard if the user is signed in
  if (session && session.user) {
    navigate('/dashboard');
  }

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkLoaded>
          <div className="clerk-signin-container">
            <SignedOut>
              <SignIn>
                <div>
                  <h2>Sign In</h2>
                  <p>Enter your credentials to sign in.</p>
                  {/* Use Clerk's openSignIn function to open the sign-in modal */}
                  <button onClick={() => openSignIn()}>Sign In</button>
                </div>
              </SignIn>
            </SignedOut>
            <SignedIn>
              <Dashboard onClose={handleCloseClick} />
            </SignedIn>
          </div>
        </ClerkLoaded>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
