import React from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn, useClerk } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();
  const { createSession } = useClerk();

  const handleCloseClick = () => {
    onClose();
  };

  const handleSignIn = async () => {
    try {
      // Create a session directly
      const { started } = await createSession();

      // Check if the sign-in process has started
      if (started) {
        // Use Clerk's event listener to detect authentication status change
        const unsubscribe = window.Clerk.addListener((resources) => {
          if (resources.session && resources.session.user) {
            // Unsubscribe to the listener once the navigation is done
            unsubscribe();

            // Redirect to the dashboard after successful sign-in
            navigate('/dashboard');
          }
        });
      } else {
        // The sign-in process did not start, handle accordingly
        console.error('Sign-in process did not start');
        navigate('/onboarding'); // For example, navigate back to onboarding
      }
    } catch (error) {
      console.error('Sign-in failed:', error);
      // Handle sign-in failure
      // For example, you can navigate back to the onboarding page
      navigate('/onboarding');
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkLoaded>
          <div className="clerk-signin-container">
            <SignedOut>
              <SignIn>
                <div>
                  <h2>Sign In</h2>
                  <p>Enter your credentials to sign in.</p>
                  <button onClick={handleSignIn}>Sign In</button>
                </div>
              </SignIn>
            </SignedOut>
            <SignedIn>
              <Dashboard onClose={handleCloseClick} />
            </SignedIn>
          </div>
        </ClerkLoaded>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
