import React, { useEffect } from 'react';
import '../App.css'

const BuyPage = () => {
  const pageContainerStyle = {
    width: '100%',
    height: '100%',
    display: 'flex',
    justifyContent: 'center', // Center horizontally
    alignItems: 'center', // Center vertically
    minHeight: '100vh', // Use minHeight instead of height to ensure it stretches to the bottom
    backgroundColor: 'rgba(255, 255, 255, 0)', // Set the background color with transparency
    overflow: 'hidden', // Prevent scrolling
  };

  const contentStyle = {
    maxWidth: '600px', // Adjust the width as needed
    padding: '20px',
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.9)', // Add a subtle shadow
    overflow: 'hidden', // Prevent scrolling
  };

  // useEffect to ensure the overflow property is set on mount
  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'visible';
    };
  }, []);

  return (
    <div style={pageContainerStyle}>
      <div style={contentStyle}>
        <h1>Buy Homes - Coming Soon!</h1>
        <p>
          We're excited to announce that the ability to buy homes is in development.
          Here are some of the features we have planned for this upcoming feature:
        </p>
        <ul>
          <li>Buy your dream home with ease</li>
          <li>Invest by buying shares in a home</li>
          <li>Rent out homes you own with our long-term rental or short-term venture options</li>
        </ul>
        <p>
          Stay tuned for updates on our progress and the launch of this exciting new feature!
        </p>
      </div>
    </div>
  );
};

export default BuyPage;
