import React from 'react';
import { ClerkLoaded, SignedIn, SignedOut, SignIn } from "@clerk/clerk-react";
import { useNavigate } from 'react-router-dom';
import Dashboard from './Dashboard';

const SignupSignInPopup = ({ onClose }) => {
  const navigate = useNavigate();

  const handleCloseClick = () => {
    onClose();
  };

  const checkSignInStatus = () => {
    try {
      // Use Clerk's isSignedIn to check if the user is signed in
      if (window.Clerk && window.Clerk.isSignedIn()) {
        // Redirect immediately after sign-in
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Error checking sign-in status:', error);
    }
  };

  // Check sign-in status when the component mounts
  checkSignInStatus();

  const handleSignIn = () => {
    try {
      // Open Clerk's sign-in modal
      window.Clerk.openSignIn({
        afterSignInUrl: '/dashboard', // Redirect to the dashboard after successful sign-in
        afterSignUpUrl: '/dashboard', // Redirect to the dashboard after successful sign-up
        redirectUrl: '/dashboard', // Redirect to the dashboard after sign-in or sign-up
        continueSignUpUrl: '/onboarding', // Redirect to the onboarding page after an incomplete sign-up
        // Add other options as needed
      });

      // Use Clerk's event listener to detect authentication status change
      const unsubscribe = window.Clerk.addListener((resources) => {
        if (resources.session && resources.session.user) {
          // You can also close the sign-in modal if needed
          window.Clerk.closeSignIn();

          // Unsubscribe to the listener once the navigation is done
          unsubscribe();

          // Redirect to the dashboard after successful sign-in
          navigate('/dashboard');
        }
      });
    } catch (error) {
      console.error('Sign-in failed:', error);
      // Handle sign-in failure
      // For example, you can navigate back to the onboarding page
      navigate('/onboarding');
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content" style={popupStyles}>
        <button className="close-button" onClick={handleCloseClick}>
          X
        </button>
        <ClerkLoaded>
          <div className="clerk-signin-container">
            <SignedOut>
              <SignIn>
                <div>
                  <h2>Sign In</h2>
                  <p>Enter your credentials to sign in.</p>
                </div>
              </SignIn>
            </SignedOut>
            <SignedIn>
              <Dashboard onClose={handleCloseClick} />
            </SignedIn>
          </div>
        </ClerkLoaded>
      </div>
    </div>
  );
};

const popupStyles = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  background: 'rgba(255, 255, 255, 0.4)',
  overflow: 'hidden',
};

export default SignupSignInPopup;
